mapper.select:
        select

        from tb_meal m
	      left join tb_seller s on m.meal_id = s.mid





                ORDER BY ${order.sort} ${order.order}


                order by m.meal_id asc


        limit #{page.offset},#{page.rows}

select
from tb_meal m from tb_meal m left join tb_seller s on m.meal_id = s.mid
where m.meal_id =#{mealId}

select m.meal_id as mealId, m.meal_name as mealName, m.meal_price as mealPrice, m.meal_num as mealNum, m.meal_picture as mealPic, m.meal_intro as mealIntro s.name as publisher
from tb_meal m left join tb_seller s on m.meal_id = s.mid
where m.meal_status = 1 order by m.meal_id desc;
