sql.where:
o.order_status !=3

    and o.order_id like '%${query.orderId}%'


    and o.order_status = #{query.orderStatus}

mapper.select:
select
from tb_order o left join tb_meal m on o.m_id = m.meal_id left join tb_meal_order mo on o.order_id = mo.order_id left join tb_address a on o.addr_id = a.id left join postman p on o.p_id = p.id left join tb_user u on o.u_id = u.user_id
ORDER BY ${order.sort} ${order.order}
order by o.order_id asc
limit #{page.offset},#{page.rows}
