sql.where:
u.user_status != 2

    and u.user_name like '%${user.userName}%'


    and u.user_level = ${user.userLevel}

mapper.select:
SELECT

FROM tb_user u
LEFT JOIN tb_address a ON u.addr_id = a.id



        ORDER BY ${order.sort}  ${order.order}



        ORDER BY u.user_id ASC


LIMIT #{page.offset},#{page.rows}
